CREATE TABLE polje( poljeid CHAR(3) UNICODE NOT NULL, obaveznost NUMBER(1) NOT NULL CHECK ( obaveznost BETWEEN 0 AND 1 ) , ponovljivost NUMBER(1) NOT NULL CHECK ( ponovljivost BETWEEN 0 AND 1 ) , po_naziv VARCHAR(160) UNICODE NOT NULL, sekundarnost NUMBER(1) NOT NULL CHECK ( sekundarnost BETWEEN 0 AND 1 ) , opis_di VARCHAR(240) UNICODE, opis_pi VARCHAR(240) UNICODE, def_pi CHAR(1) UNICODE, def_di CHAR(1) UNICODE, PRIMARY KEY (POLJEID))
CREATE TABLE tipekst_sifarnika( tesid NUMBER(2) NOT NULL CHECK ( tesid BETWEEN 0 AND 99 ) , tes_naziv VARCHAR(160) UNICODE NOT NULL, PRIMARY KEY (TESID))
CREATE TABLE tip_kontrole( kontrid NUMBER(2) NOT NULL CHECK ( kontrid BETWEEN 0 AND 99 ) , ko_naziv VARCHAR(160) UNICODE NOT NULL, PRIMARY KEY (KONTRID))
CREATE TABLE potpolja( po_poljeid CHAR(3) UNICODE NOT NULL, potpoljaid CHAR(1) UNICODE NOT NULL, kontrola_kontrid NUMBER(2) NOT NULL CHECK ( kontrola_kontrid BETWEEN 0 AND 99 ) , obaveznostpp NUMBER(1) NOT NULL CHECK ( obaveznostpp BETWEEN 0 AND 1 ) , ponovljivostpp NUMBER(1) NOT NULL CHECK ( ponovljivostpp BETWEEN 0 AND 1 ) , pp_naziv VARCHAR(160) UNICODE NOT NULL, duzina NUMBER(2) NOT NULL CHECK ( duzina BETWEEN 0 AND 99 ) , status CHAR(1) UNICODE NOT NULL, tes_tesid NUMBER(2), defvrednost VARCHAR(15) UNICODE NULL, PRIMARY KEY (POTPOLJAID, PO_POLJEID), FOREIGN KEY (PO_POLJEID) REFERENCES POLJE ( POLJEID) ON DELETE CASCADE, FOREIGN KEY (TES_TESID) REFERENCES TIPEKST_SIFARNIKA ( TESID), FOREIGN KEY (KONTROLA_KONTRID) REFERENCES TIP_KONTROLE ( KONTRID))
CREATE TABLE potpotpolja( ppo_potpoljaid CHAR(1) UNICODE NOT NULL, ppo_po_poljeid CHAR(3) UNICODE NOT NULL, potpid CHAR(1) UNICODE NOT NULL, kontrola_kontrid NUMBER(2) NOT NULL CHECK ( kontrola_kontrid BETWEEN 0 AND 99 ) , potp_naziv VARCHAR(160) UNICODE NOT NULL, potpduzina NUMBER(2) NOT NULL CHECK ( potpduzina BETWEEN 0 AND 99 ) , potponov NUMBER(1) NOT NULL CHECK ( potponov BETWEEN 0 AND 1 ) , potpobavez NUMBER(1) NOT NULL CHECK ( potpobavez BETWEEN 0 AND 1 ) , statusppp CHAR(1) UNICODE NOT NULL, potpdefvr VARCHAR(15) UNICODE, PRIMARY KEY (POTPID, PPO_PO_POLJEID, PPO_POTPOLJAID), FOREIGN KEY (PPO_POTPOLJAID, PPO_PO_POLJEID) REFERENCES POTPOLJA ( POTPOLJAID, PO_POLJEID) ON DELETE CASCADE, FOREIGN KEY (KONTROLA_KONTRID) REFERENCES TIP_KONTROLE ( KONTRID))
CREATE TABLE drugi_indikator( po_poljeid CHAR(3) UNICODE NOT NULL, drindid CHAR(1) UNICODE NOT NULL, di_naziv VARCHAR(160) UNICODE NOT NULL, PRIMARY KEY (DRINDID, PO_POLJEID), FOREIGN KEY (PO_POLJEID) REFERENCES POLJE ( POLJEID) ON DELETE CASCADE)
CREATE TABLE eksterni_sifarnik( tes_tesid NUMBER(2) NOT NULL CHECK ( tes_tesid BETWEEN 0 AND 99 ), esid CHAR(15) UNICODE NOT NULL, es_naziv VARCHAR(160) UNICODE NOT NULL, PRIMARY KEY (ESID, TES_TESID), FOREIGN KEY (TES_TESID) REFERENCES TIPEKST_SIFARNIKA ( TESID) ON DELETE CASCADE)
CREATE TABLE interni_sifarnik( ppo_potpoljaid CHAR(1) UNICODE NOT NULL, ppo_po_poljeid CHAR(3) UNICODE NOT NULL, isid CHAR(15) UNICODE NOT NULL, is_naziv VARCHAR(160) UNICODE NOT NULL, PRIMARY KEY (ISID, PPO_PO_POLJEID, PPO_POTPOLJAID), FOREIGN KEY (PPO_POTPOLJAID, PPO_PO_POLJEID) REFERENCES POTPOLJA ( POTPOLJAID, PO_POLJEID) ON DELETE CASCADE)
CREATE TABLE prvi_indikator( po_poljeid CHAR(3) UNICODE NOT NULL, prindid CHAR(1) UNICODE NOT NULL, pi_naziv VARCHAR(160) UNICODE NOT NULL, PRIMARY KEY (PRINDID, PO_POLJEID), FOREIGN KEY (PO_POLJEID) REFERENCES POLJE ( POLJEID) ON DELETE CASCADE)
CREATE TABLE sifarnik_potpotpolja( ppp_potpid CHAR(1) UNICODE NOT NULL, ppp_ppo_potpoljaid CHAR(1) UNICODE NOT NULL, ppp_ppo_po_poljeid CHAR(3) UNICODE NOT NULL, sifpotpid CHAR(15) UNICODE NOT NULL, sifpotp_naziv VARCHAR(160) UNICODE NOT NULL, PRIMARY KEY (SIFPOTPID, PPP_PPO_PO_POLJEID, PPP_PPO_POTPOLJAID, PPP_POTPID), FOREIGN KEY (PPP_POTPID, PPP_PPO_PO_POLJEID, PPP_PPO_POTPOLJAID) REFERENCES POTPOTPOLJA ( POTPID, PPO_PO_POLJEID, PPO_POTPOLJAID) ON DELETE CASCADE)
CREATE INDEX DIN_PO_FK_I ON DRUGI_INDIKATOR( po_poljeid )
CREATE INDEX ES_TES_FK_I ON EKSTERNI_SIFARNIK( tes_tesid )
CREATE INDEX IS_PPO_FK_I ON INTERNI_SIFARNIK( ppo_potpoljaid , ppo_po_poljeid )
CREATE INDEX PIN_PO_FK_I ON PRVI_INDIKATOR( po_poljeid )
CREATE INDEX PPO_KONTROLA_FK_I ON POTPOLJA( kontrola_kontrid )
CREATE INDEX PPO_PO_FK_I ON POTPOLJA( po_poljeid )
CREATE INDEX PPO_TES_FK_I ON POTPOLJA( tes_tesid )
CREATE INDEX PPP_KONTROLA_FK_I ON POTPOTPOLJA( kontrola_kontrid )
CREATE INDEX PPP_PPO_FK_I ON POTPOTPOLJA( ppo_potpoljaid , ppo_po_poljeid )
CREATE INDEX SPP_PPP_FK_I ON SIFARNIK_POTPOTPOLJA( ppp_potpid , ppp_ppo_po_poljeid , ppp_ppo_potpoljaid )
